
public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		int k = 2;
		
		if (i == k)
			System.out.println("IF i EQUALS k");
		if (i != k)
			System.out.println("IF i NOT-EQUALS k");
		if (i > k)
			System.out.println("IF i GREATER THAN k");
		if (i < k)
			System.out.println("IF i LESS THAN k");
	}

}
