
public class ConditionalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10;
		int k = 20;
		
		if ((i == 10) && (k == 10))
			System.out.println("IF i EQUALS 10 OR k EQUALS 20");
		if ((i == 10) || (k == 10))
			System.out.println("IF i EQUALS 10 OR k EQUALS 10");

	}

}
