
public class OneDimensionalArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr = new int[4];
		 arr[0] = 10;
		 arr[2] = 30;
		 arr[3] = 40;
		 arr[1] = 20;

		 System.out.println(arr[0]);
		 System.out.println(arr[1]);
		 System.out.println(arr[2]);
		 System.out.println(arr[3]);

	}

}
