public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = 'a';
		byte b = 2;
		short c = 22;
		int d = 45;
		float e = 5.123451f;
		double f = 4.233474864d;
		boolean g = true;

		System.out.println("Char a value is: "+a);
		System.out.println("Byte b value is: "+b);
		System.out.println("Short c value is: "+c);
		System.out.println("Int d value is: "+d);
		System.out.println("Float e value is: "+e);
		System.out.println("Double f value is: "+f);
		System.out.println("Boolean g value is: "+g);
	}

}
