
public class UnaryDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int result = +1;
		System.out.println("Result: "+result);
		
		result--;
		System.out.println("Result: "+result);
		
		result++;
		System.out.println("Result: "+result);
		
		result = -result;
		System.out.println("Result: "+result);
		
		boolean success = false;
		System.out.println("Success: "+success);
		System.out.println("Success: "+!success);

		
	}

}
